mapper.select:
SELECT a.vid, a.p_code as pastorCode, c.name as pastorName, a.title, a.create_ymd as createYmd , a.ovid, b.name as originName, a.youtubeid as youtubeId, a.upddt as updDt FROM tb_youtube_pastor a inner join tb_origin_vid b on a.ovid = b.ovid inner join tb_pastor c on a.p_code = c.p_code where 1=1
AND a.p_code = #{pastorCode}
and a.ovid = #{ovid}
order by a.upddt desc

mapper.insert:
INSERT INTO tb_youtube_pastor
(vid, p_code, title, create_ymd, ovid, youtubeid, regid, regdt, updid, upddt)
select concat('V', #{pastorCode}, lpad(cast(substring(ifnull(max(vid), concat('V', #{pastorCode}, '00000')),9) as unsigned)+1, 5, '0')) as vid
    , #{pastorCode}, #{title}, date_format(now(), '%Y%m%d'), #{ovid}, #{youtubeId}
    , #{userId}, now(), #{userId}, now()
from tb_youtube_pastor where p_code = #{pastorCode}

mapper.update:
UPDATE cihdb.tb_youtube_pastor
SET p_code=#{pastorCode}, title=#{title}, ovid=#{ovid}, youtubeid=#{youtubeId}
    , updid=#{userId}, upddt=now()
WHERE vid=#{vid};

UPDATE tb_origin_vid
SET name=#{name}, title=#{title}, channelurl=#{channelUrl}, comment=#{comment}, updid=#{userId}, upddt=now()
WHERE ovid=#{ovid}
