mapper.insert:
INSERT INTO tb_youtube_pastor
(vid, p_code, channel_id, title, channel_title, thumbnail_d, thumbnail_m, thumbnail_h
    , grade, sort, description, create_ymd, publishedAt
    , prevPageToken, nextPageToken, totalResults, itemNo, qSignal, shorts
    , regid, regdt, updid, upddt, main_yn)
values ( #{vid}, #{pastorCode}, #{channelId}, #{title}, #{channelTitle}, #{thumbnailDefault}, #{thumbnailMedium}, #{thumbnailHigh}
    , #{grade}, #{sort}, #{description}, date_format(now(), '%Y%m%d'), #{publishedAt}
    , #{prevPageToken}, #{nextPageToken}, #{totalResults}, #{itemNo}, #{qSignal}, #{shorts}
    , #{userId}, now(), #{userId}, now(), #{mainYn})
ON DUPLICATE KEY UPDATE p_code=#{pastorCode}, grade=#{grade}, sort=#{sort}, shorts=#{shorts}, qSignal=#{qSignal}
    , updid=#{userId}, upddt=current_timestamp(), main_yn=#{mainYn}

mapper.update:
UPDATE tb_youtube_pastor
SET p_code=#{pastorCode}, channel_id=#{channelId}, title=#{title}
    , channel_title=#{channelTitle}, thumbnail_d=#{thumbnailDefault}, thumbnail_m=#{thumbnailMedium}, thumbnail_h=#{thumbnailHigh}
    , grade=#{grade}, sort=#{sort}, description= #{description}
    , updid=#{userId}, upddt=current_timestamp(), main_yn=#{mainYn}
WHERE vid=#{vid}

mapper.select:
select concat((y1+y2+y3+y4+y5), ' P:', y1, ' C:', y2, ' S:', y3, ' F:', y4, ' M:', y5) as itemsCount, m.* from ( SELECT a.channel_id as channelId, a.title as channelTitle, a.description as channelDescription , concat(substr(a.description,1, 100), '...') as shortDescription, a.regid as userId, a.updid as updId, a.upddt as updDt , (select count(0) from tb_youtube_pastor b where b.channel_id = a.channel_id) as y1 , (select count(0) from tb_youtube_celeb b where b.channel_id = a.channel_id) as y2 , (select count(0) from tb_youtube_sermon b where b.channel_id = a.channel_id) as y3 , (select count(0) from tb_youtube_faith b where b.channel_id = a.channel_id) as y4 , (select count(0) from tb_shorts_ccm b where b.channel_id = a.channel_id) as y5 FROM tb_origin_vid a where 1=1
and a.title like concat('%', #{channelTitle}, '%')
) m order by m.upddt desc

mapper.update:
UPDATE tb_origin_vid
SET title=#{channelTitle}, description=#{channelDescription}, updid=#{userId}, upddt=now()
WHERE channel_id=#{channelId}
