mapper.select:
select a.ym, a.pseq as prayerSeq, a.cat, a.prayer, a.regid as regId, a.regdt as regDt, a.updid as updId, a.upddt as updDt from tb_dogo_prayer a
and ym like concat('%', #{ym}, '%')
and cat = #{cat}
and prayer like concat('%', #{prayer}, '%')
and pseq = #{prayerSeq}
order by a.ym, a.pseq

SELECT a.pseq as pSeq, a.uid, a.start_dt as startDt, a.end_dt as endDt, a.prayer, a.regdt as regDt, a.upddt as updDt FROM tb_user_prayer a
and a.uid like concat('%', #{uid}, '%')
and a.start_dt >= #{startDt}
and a.end_dt <= #{endDt}
and prayer like concat('%', #{prayer}, '%')
order by a.pseq desc

choose.otherwise:
UPDATE tb_user_prayer
SET uid=#{uid}, start_dt=#{startDt}, end_dt=#{endDt}, prayer=#{prayer}, upddt=now()
WHERE pseq=#{prayerSeq}
